Generate Operation Code Name.command:
let finalMessage = 'Operation ';
let part1 = '';
let part2 = '';

let pack = await game.packs.get("deltagreen.operation-code-name-generator-tables").getDocuments();

let combinationsTable = await pack.find(t => t.name === 'Codename Part Combinations');
let adjectiveTable = await pack.find(t => t.name === 'Codename Generator - Adjective');
let colorTable = await pack.find(t => t.name === 'Codename Generator - Color');
let nounTable = await pack.find(t => t.name === 'Codename Generator - Noun');
let phoneticTable = await pack.find(t => t.name === 'Codename Generator - Phonetic');

let rollResult = await combinationsTable.roll();

let randomCombinationResult = rollResult.results[0].text;


if(randomCombinationResult === 'Color-Noun'){
    rollResult = await colorTable.roll();

    part1 = rollResult.results[0].text;

    rollResult = await nounTable.roll();

    part2 = rollResult.results[0].text;
}
else if(randomCombinationResult === 'Adjective-Noun'){
    rollResult = await adjectiveTable.roll();

    part1 = rollResult.results[0].text;

    rollResult = await nounTable.roll();

    part2 = rollResult.results[0].text;

    // if identical noun, re-try once
    if(part1 === part2){
        rollResult = await nounTable.roll();
        part2 = rollResult.results[0].text;
    }
}
else if(randomCombinationResult === 'Noun-Noun'){
    rollResult = await nounTable.roll();

    part1 = rollResult.results[0].text;

    rollResult = await nounTable.roll();

    part2 = rollResult.results[0].text;
}
else if(randomCombinationResult === 'Phonetic-Noun'){
    rollResult = await phoneticTable.roll();

    part1 = rollResult.results[0].text;

    rollResult = await nounTable.roll();

    part2 = rollResult.results[0].text;
}

finalMessage += part1 + ' ' + part2;

let messageData = {
    flavor: finalMessage
};

ChatMessage.create(messageData);
